import React from "react";
import { Container } from "react-bootstrap";

const AUTH_URL = `https://accounts.spotify.com/authorize?client_id=${process.env.REACT_API_KEY}&response_type=code&redirect_uri=http://localhost:3000&scope=streaming%20user-read-email%20user-read-private%20user-library-read%20user-library-modify%20user-read-playback-state%20user-modify-playback-state`;

const Logins = () => {
  return (
    <Container
      className="d-felx justify-center align-items-center"
      style={{ minHeight: "100vh" }}
    >
      <a className="btn btn-success btn-lg" href={AUTH_URL.URL}>
        LOG IN WITH SPOTIFY
      </a>
    </Container>
  );
};

export default Logins;
